import React from 'react';

const flags = [
  { code: 'en', label: 'ðŸ‡¬ðŸ‡§' },
  { code: 'es', label: 'ðŸ‡ªðŸ‡¸' },
  { code: 'fr', label: 'ðŸ‡«ðŸ‡·' },
];

interface LanguageSwitcherProps {
  current: string;
  onChange: (lang: string) => void;
}

const LanguageSwitcher: React.FC<LanguageSwitcherProps> = ({ current, onChange }) => (
  <div className="fixed top-4 right-4 z-50 flex gap-2 bg-white/80 rounded-full shadow px-3 py-2 border border-slate-200">
    {flags.map(flag => (
      <button
        key={flag.code}
        onClick={() => onChange(flag.code)}
        className={`text-2xl transition-transform duration-100 focus:outline-none ${current === flag.code ? 'scale-125' : 'opacity-70 hover:opacity-100'}`}
        aria-label={flag.code}
      >
        {flag.label}
      </button>
    ))}
  </div>
);

export default LanguageSwitcher;
