import React from 'react';

const steps = [
  {
    icon: 'ðŸ’¡',
    title: 'Connect',
    description: 'Link your calendar and project tools.'
  },
  {
    icon: 'ðŸ¤–',
    title: 'Automate',
    description: 'Let the AI summarize meetings and track tasks.'
  },
  {
    icon: 'ðŸ“ˆ',
    title: 'Grow',
    description: 'Get instant reports and insights to improve your workflow.'
  }
];

const HowItWorks = () => (
  <section className="w-full py-16 bg-slate-50 flex flex-col items-center">
    <h2 className="text-3xl font-bold text-gray-900 mb-10">How it works</h2>
    <div className="flex flex-col md:flex-row gap-8 justify-center">
      {steps.map((step, idx) => (
        <div
          key={idx}
          className="flex flex-col items-center bg-white rounded-xl shadow p-8 w-64 border-t-4 border-blue-500"
        >
          <div className="text-4xl mb-4">{step.icon}</div>
          <h3 className="text-lg font-semibold mb-2 text-gray-800">{step.title}</h3>
          <p className="text-gray-600 text-center">{step.description}</p>
        </div>
      ))}
    </div>
  </section>
);

export default HowItWorks;
