import React from 'react';

type HeroProps = {
  t: {
    headline: string;
    subtext: string;
    cta: string;
  };
};

const Hero: React.FC<HeroProps> = ({ t }) => (
  <section className="w-full min-h-[60vh] flex flex-col items-center justify-center bg-gradient-to-b from-white to-slate-100 py-16 px-4 text-center">
    <h1 className="text-4xl md:text-6xl font-extrabold text-gray-900 mb-4 tracking-tight">
      {t.headline}
    </h1>
    <p className="text-lg md:text-2xl text-gray-600 mb-8 max-w-2xl">
      {t.subtext}
    </p>
    <a
      href="#"
      className="inline-block bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg px-8 py-3 shadow-lg transition-colors duration-200 text-lg"
    >
      {t.cta}
    </a>
  </section>
);

export default Hero;
