import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

interface Task {
  id: string;
  title: string;
  description: string;
  status: 'To Do';
  assignee?: string;
  dueDate?: string;
  priority?: 'Low' | 'Medium' | 'High';
}

const TaskBoard: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [form, setForm] = useState({
    title: '',
    description: '',
    assignee: '',
    dueDate: '',
    priority: 'Medium',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!form.title.trim()) return;
    setTasks([
      ...tasks,
      {
        id: uuidv4(),
        title: form.title,
        description: form.description,
        status: 'To Do',
        assignee: form.assignee,
        dueDate: form.dueDate,
        priority: form.priority as 'Low' | 'Medium' | 'High',
      },
    ]);
    setForm({ title: '', description: '', assignee: '', dueDate: '', priority: 'Medium' });
  };

  return (
    <div className="max-w-3xl mx-auto my-10 p-4 bg-white rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-6">To Do</h2>
      <form onSubmit={handleSubmit} className="mb-8 grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          name="title"
          value={form.title}
          onChange={handleChange}
          placeholder="Task title"
          className="border rounded px-3 py-2"
          required
        />
        <input
          name="assignee"
          value={form.assignee}
          onChange={handleChange}
          placeholder="Assignee (optional)"
          className="border rounded px-3 py-2"
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Description"
          className="border rounded px-3 py-2 md:col-span-2"
        />
        <input
          type="date"
          name="dueDate"
          value={form.dueDate}
          onChange={handleChange}
          className="border rounded px-3 py-2"
        />
        <select
          name="priority"
          value={form.priority}
          onChange={handleChange}
          className="border rounded px-3 py-2"
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button
          type="submit"
          className="md:col-span-2 bg-blue-600 text-white rounded px-4 py-2 font-semibold hover:bg-blue-700 transition"
        >
          Add Task
        </button>
      </form>
      <div className="space-y-4">
        {tasks.length === 0 && <div className="text-gray-400">No tasks yet.</div>}
        {tasks.map(task => (
          <div key={task.id} className="bg-slate-50 rounded-lg p-4 shadow flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <div className="font-semibold text-lg">{task.title}</div>
              <div className="text-gray-500 text-sm mb-1">{task.description}</div>
              <div className="text-xs text-gray-400">
                {task.assignee && <>ðŸ‘¤ {task.assignee} &nbsp;</>}
                {task.dueDate && <>ðŸ“… {task.dueDate} &nbsp;</>}
                <span className={`font-bold ${task.priority === 'High' ? 'text-red-500' : task.priority === 'Low' ? 'text-green-600' : 'text-yellow-600'}`}>{task.priority}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskBoard;
