import React, { useState, useRef, useEffect } from 'react';

const flags = [
  { code: 'en', label: 'ðŸ‡¬ðŸ‡§' },
  { code: 'es', label: 'ðŸ‡ªðŸ‡¸' },
  { code: 'fr', label: 'ðŸ‡«ðŸ‡·' },
];

interface LanguageSwitcherProps {
  current: 'en' | 'es' | 'fr';
  onChange: (lang: 'en' | 'es' | 'fr') => void;
}

const LanguageSwitcher: React.FC<LanguageSwitcherProps> = ({ current, onChange }) => {
  const [open, setOpen] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  // Close dropdown on outside click
  useEffect(() => {
    const handleClick = (e: MouseEvent) => {
      if (ref.current && !ref.current.contains(e.target as Node)) {
        setOpen(false);
      }
    };
    if (open) document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [open]);

  const currentFlag = flags.find(f => f.code === current)!;
  const otherFlags = flags.filter(f => f.code !== current);

  return (
    <div ref={ref} className="fixed top-4 right-4 z-50">
      <button
        onClick={() => setOpen(o => !o)}
        className="text-2xl bg-white/80 rounded-full shadow px-3 py-2 border border-slate-200 focus:outline-none hover:scale-110 transition-transform"
        aria-label="Change language"
      >
        {currentFlag.label}
      </button>
      {open && (
        <div className="absolute right-0 mt-2 bg-white rounded-lg shadow-lg border border-slate-200 flex flex-col py-1 animate-fade-in">
          {otherFlags.map(flag => (
            <button
              key={flag.code}
              onClick={() => { onChange(flag.code as 'en' | 'es' | 'fr'); setOpen(false); }}
              className="text-2xl px-4 py-2 hover:bg-slate-100 focus:outline-none text-left"
              aria-label={flag.code}
            >
              {flag.label}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default LanguageSwitcher;
