import React from 'react';

const features = [
  {
    icon: '📝',
    title: 'AI Meeting Summaries',
    description: 'Get concise, actionable summaries of your meetings instantly.'
  },
  {
    icon: '📋',
    title: 'Smart Task Tracking',
    description: 'Track project tasks automatically and never miss a deadline.'
  },
  {
    icon: '📊',
    title: 'KPI & Report Generator',
    description: 'Generate beautiful reports and monitor KPIs with a click.'
  }
];

const Features = () => (
  <section className="w-full py-16 bg-white flex flex-col items-center">
    <h2 className="text-3xl font-bold text-gray-900 mb-10">Features</h2>
    <div className="flex flex-col md:flex-row gap-8 justify-center">
      {features.map((feature, idx) => (
        <div
          key={idx}
          className="flex flex-col items-center bg-slate-50 rounded-xl shadow-md p-8 w-72 hover:shadow-lg transition-shadow"
        >
          <div className="text-5xl mb-4">{feature.icon}</div>
          <h3 className="text-xl font-semibold mb-2 text-gray-800">{feature.title}</h3>
          <p className="text-gray-600 text-center">{feature.description}</p>
        </div>
      ))}
    </div>
  </section>
);

export default Features;
