import React, { useState } from 'react';
import Hero from './components/Hero';
import Features from './components/Features';
import HowItWorks from './components/HowItWorks';
import TechStack from './components/TechStack';
import Footer from './components/Footer';
import LanguageSwitcher from './components/LanguageSwitcher';
import translations from './translations';
import ProjectBoard from './components/ProjectBoard';

function App() {
  const [lang, setLang] = useState<'en' | 'es' | 'fr'>('en');
  const [showTaskBoard, setShowTaskBoard] = useState(false);
  const t = translations[lang];

  if (showTaskBoard) {
    return (
      <div className="bg-slate-50 min-h-screen flex flex-col">
        <LanguageSwitcher current={lang} onChange={setLang} />
        <ProjectBoard />
        <button
          className="fixed top-4 left-4 bg-white border border-slate-200 rounded-full shadow px-4 py-2 text-sm font-semibold hover:bg-slate-100 transition"
          onClick={() => setShowTaskBoard(false)}
        >
          ← Back to landing
        </button>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen flex flex-col">
      <LanguageSwitcher current={lang} onChange={setLang} />
      <Hero t={t.hero} onStart={() => setShowTaskBoard(true)} />
      <Features t={t.features} />
      <HowItWorks t={t.howItWorks} />
      <TechStack t={t.techStack} />
      <Footer t={t.footer} />
    </div>
  );
}

export default App;
